DELETE FROM theme_molecule_map tmm
              WHERE     tmm.theme_no = :old.theme_no
                    AND tmm.molecule_id = :old.molecule_id;

DELETE FROM theme_molecule_map tmm
                          WHERE     tmm.theme_no = :new.theme_no
                                AND tmm.molecule_id = :old.molecule_id;

DELETE FROM theme_molecule_map tmm
                      WHERE     tmm.theme_no = :new.theme_no
                            AND tmm.molecule_id = :old.molecule_id;
